import React from 'react';
import styles from './users.module.css'
import userPict from '../../assets/images/images.png'
import {NavLink} from "react-router-dom";

let Users = (props) => {

    let pagesCount = Math.ceil(props.totalUsersCount / props.pageSize);
    let pages = [];
    for (let i = 1; i <= pagesCount; i++) {
        pages.push(i)
    }

    return <div>
        <div>
            {pages.map(page => {
                return <span className={props.currentPage === page && styles.boldPage}
                             onClick={() => {
                                 props.onPageChanged(page)
                             }}>{page}</span>
            })}
        </div>
        {
            props.users.map(u => <div key={u.id}>
            <span>
                <div> <NavLink to={'/profile/' + u.id}>
                    <img src={u.photos.small != null ? u.photos.small : userPict} className={styles.userFoto}/>
                </NavLink>
                </div>
                <div>
{u.followed
    ? <button disabled={props.followingProgress.some(id => id === u.id)} onClick={() => {
                props.unfollow(u.id)
                }}>unfollow
    </button>

    : <button disabled={props.followingProgress.some(id => id === u.id)} onClick={() => {
                props.follow(u.id)
                }}>follow</button>}
            </div>
            </span>
                <span>
                <span>
    <div>{u.name}</div>
    <div>{u.status}</div>
</span>
            <span>
                  <div>{'u.location.country'}</div>
                    <div>{'u.location.city'}</div>
                </span>
                </span>
            </div>)
        }
    </div>
}

export default Users;
