import React from 'react';
import s from './ProfileInfo.module.css';

class ProfileStatus extends React.Component {

    state = {
        editMode: false
    }

    activatedMode () {
        this.setState({ editMode: true });
    }

    deactivatedMode () {
        this.setState({ editMode: false });
    }

    render() {
        return (
            <div>
                {!this.state.editMode &&
                    <div>
                        <div onDoubleClick={this.activatedMode.bind(this)}> {this.props.status} </div>
                    </div>
                }
                {this.state.editMode &&
                    <div>
                        <input onBlur={this.deactivatedMode.bind(this)}
                               value={this.props.status}
                        autoFocus={true}/>
                    </div>
                }
            </div>
        )
    }
}

export default ProfileStatus;
